package shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools;import android.app.Activity;import android.widget.EditText;import android.widget.TextView;public class EditTextUnt {    /**     * 直接使用找到TextView     * @param Et     * @return     */    public static handlerEdittext with(EditText Et) {        handlerEdittext handlerEdittext = new handlerEdittext();        handlerEdittext.get(Et);        return handlerEdittext;    }    /**     * 通过Activity来整理字体     *     * @param _class Activity     * @param _id    ID名称     * @return     */    public static handlerText with(Activity _class, int _id) {        handlerText handlerText = new handlerText();        TextView tv = _class.findViewById(_id);        return handlerText.get(tv);    }}