package shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools;import android.app.Activity;import android.util.Log;import android.view.View;import android.widget.LinearLayout;public class LinearLayoutUnt {    private static String MSG = "LinearlayoutUnt.java[+]";    public static handlerLayout with(LinearLayout _layout) {        try {            handlerLayout relativeLayoutUnt = new handlerLayout();            relativeLayoutUnt.get(_layout);            return relativeLayoutUnt;        } catch (Exception e) {            Log.e(MSG, "设置LinearlayoutUnt失败" + e.getMessage());            return null;        }    }    /**     * 用控件ID找控件     * @param item     * @param _Id     * @return     */    public static handlerLayout with(View item, int _Id) {        try {            LinearLayout _layout = item.findViewById(_Id);            handlerLayout _handlerLayout = new handlerLayout();            _handlerLayout.get(_layout);            return _handlerLayout;        } catch (Exception e) {            Log.e(MSG, "设置LinearlayoutUnt失败" + e.getMessage());            return null;        }    }    /**     * 通过窗口找ID控件     * @param _activity     * @param _Id     * @return     */    public static handlerLayout with(Activity _activity, int _Id){        try {            LinearLayout _layout = _activity.findViewById(_Id);            handlerLayout _handlerLayout = new handlerLayout();            _handlerLayout.get(_layout);            return _handlerLayout;        } catch (Exception e) {            Log.e(MSG, "设置LinearlayoutUnt失败" + e.getMessage());            return null;        }    }}