package shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools;import android.content.Context;import android.content.Intent;import android.util.Log;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.WebServiceAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyPage.WEB_VALUES_ACT;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyPage.WINDOW_PAGE;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Config;import shlm.lmcs.com.lazycat.LazyShopAct.ShowshopOffice;import shlm.lmcs.com.lazycat.LazyShopValues.LocalValues;/** * 用来处理一段字符是否打开的是系统内的窗口 还是打开网页 */public class ToOpenWindowtools {    private Context _Context;/*上下文对象*/    private String _Text;/*字符串*/    private String MSG = "ToOpenWindowtools.java[+]";    public ToOpenWindowtools(Context _context, String _text) {        if (_context != null && _text != null) {            this._Context = _context;            this._Text = _text;        }    }    public void toStart() {        if (this._Text.indexOf("http") != -1) {            try {                /*要用WEB_VALUES打开*/                WEB_VALUES_ACT web_values_act = new WEB_VALUES_ACT(this._Text.trim());                web_values_act.set_TitleBackColor("#08c299");                web_values_act.set_TitleColor("#ffffff");                web_values_act.set_StaticColor("#08c299");                Intent i = new Intent();                i.setClass(_Context, WebServiceAct.class);                i.putExtra(WINDOW_PAGE.RESULT_WEBVIEW, web_values_act);                i.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK);                _Context.startActivity(i);            } catch (Exception e) {                Log.e(MSG, "处理跳转的判断失败" + e.getMessage());            }        } else {            try {                /*用窗口打开*/                Intent intent = new Intent();                intent.putExtra(Config.Windows.GET_WINDOW_VALUE_SHOP_MESSAGE, this._Text.trim());                intent.putExtra(Config.Windows.GET_WINDOW_VALUE_SHOP_ACTION, LocalValues                        .VALUES_SEARCH.VALUES_TO_SEARCH_SHOPKEYWORD);                intent.setClass(_Context, ShowshopOffice.class);                _Context.startActivity(intent);            } catch (Exception e) {                Log.e(MSG, "处理跳转的判断失败" + e.getMessage());            }        }    }}