package shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools;import android.content.Context;import android.graphics.Typeface;import android.widget.EditText;public class handlerEdittext {    public static EditText Et;    private static handlerEdittext handlerEdittext = new handlerEdittext();    public handlerEdittext get(EditText _Et) {        this.Et = _Et;        return handlerEdittext;    }    /**     * 设置字体     *     * @return     */    public handlerEdittext setFont(Context _Context, String _Fontfile) {        Typeface typeface = Typeface.createFromAsset(_Context.getAssets(), _Fontfile);        this.Et.setTypeface(typeface);        return handlerEdittext;    }    /**     * 获取焦点     *     * @return     */    public handlerEdittext requestFocus() {        this.Et.requestFocus();        return handlerEdittext;    }    /**     * 设置文字信息     */    public handlerEdittext setText(String _String) {        this.Et.setText(_String.trim());        return handlerEdittext;    }}