package shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools;import android.util.Log;import android.view.View;import android.widget.LinearLayout;public class handlerLayout {    private static handlerLayout _handlerLayout = new handlerLayout();    public LinearLayout _layout;    private String MSG = "handlerLayout.java[+]";    public handlerLayout get(LinearLayout layout) {        this._layout = layout;        return _handlerLayout;    }    /**     * 设置是否显示     */    public handlerLayout setVisibility(Boolean _b) {        if (_b != null) {            if (_b) {                _layout.setVisibility(View.VISIBLE);            } else {                _layout.setVisibility(View.GONE);            }        } else {            Log.e(MSG, "设置是否显示为空");        }        return _handlerLayout;    }}