package shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools;import android.util.Log;import android.view.View;import android.widget.RelativeLayout;public class handlerRelative {    private String MSG = "handlerRelative.java[+]";    private static handlerRelative _handlerRelative = new handlerRelative();    public static RelativeLayout _RelativeLayout;    /**     * 初始化     *     * @param _relativelayout     * @return     */    public handlerRelative get(RelativeLayout _relativelayout) {        this._RelativeLayout = _relativelayout;        return _handlerRelative;    }    /**     * 设置监听事件     */    public handlerRelative setOnclick(View.OnClickListener _onclick) {        if (_onclick != null) {            this._RelativeLayout.setOnClickListener(_onclick);        } else {            Log.e(MSG, "监听事件为空");        }        return _handlerRelative;    }    public handlerRelative setTag(Object _tag) {        if (_tag != null) {            this._RelativeLayout.setTag(_tag);        } else {            Log.e(MSG, "要设置的TAG为空");        }        return _handlerRelative;    }    /*设置是否显示*/    public handlerRelative setVisibility(Boolean isTure) {        try {            if (isTure && isTure != null) {                this._RelativeLayout.setVisibility(View.VISIBLE);            } else {                this._RelativeLayout.setVisibility(View.GONE);            }        } catch (Exception e) {            Log.e(MSG, "设置是否显示控件错误," + e.getMessage());        }        return _handlerRelative;    }}