package shlm.lmcs.com.lazycat.LazyCatProgramUnt.Views;import android.content.Context;import android.util.AttributeSet;import android.util.Log;import android.view.MotionEvent;import android.widget.FrameLayout;/** * 监听左右滑动的Fragment */public class FragmentLR extends FrameLayout {    private int _downX;    private int _countX;    private Listener _Listener;    private String MSG = "FragmentLR.java[+]";    public FragmentLR(Context context) {        super(context);    }    public FragmentLR(Context context, AttributeSet attrs) {        super(context, attrs);    }    public FragmentLR(Context context, AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);    }    @Override    public boolean dispatchTouchEvent(MotionEvent ev) {        switch (ev.getAction()) {            case MotionEvent.ACTION_DOWN:                _downX = (int) ev.getX();                Log.i(MSG, "启动");                break;            case MotionEvent.ACTION_MOVE:                _countX = (int) ((ev.getX() - _downX) * 1 / 2);                Log.i(MSG, "滑动距离" + _countX);                break;            case MotionEvent.ACTION_UP:                Log.i(MSG, "滑动距离" + _countX);                if (_countX < 0) {                    if (this._Listener != null) {                        this._Listener.onScrollLeft(Math.abs(_countX));                    }                } else {                    if (this._Listener != null) {                        this._Listener.onScrollRight(Math.abs(_countX));                    }                }                break;        }        return super.dispatchTouchEvent(ev);    }    public void SetListener(Listener listener) {        this._Listener = listener;    }    public interface Listener {        void onScrollLeft(int moveCount);/*移动到左边*/        void onScrollRight(int moveCount);/*移动到右边*/    }}