package shlm.lmcs.com.lazycat.LazyCatProgramUnt.Views;import android.annotation.SuppressLint;import android.content.Context;import android.util.AttributeSet;import android.util.Log;import android.widget.TextView;@SuppressLint("AppCompatCustomView")public class Numberincrease extends TextView implements Runnable {    private static final String MSG = "Numberincrease.java[+]";    private static final int changeCount = 80;    private int textColor = 0xf30d88;    private float startValue = 0.00f;    private float startAverage = 0.00f;    private int count = 0;    public Numberincrease(Context context) {        super(context);    }    public Numberincrease(Context context, AttributeSet attrs) {        super(context, attrs);        startAverage = startValue / changeCount;        setText(String.valueOf(0.00f));    }    public Numberincrease(Context context, AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);    }    public void setValues(float _values) {        startAverage = _values / changeCount;    }    public double getValues() {        return startValue;    }    @Override    public void run() {        while (count < changeCount) {            count++;            startValue += startAverage;            try {                Thread.sleep(10);            } catch (Exception e) {                Log.e(MSG, "动态的数字增加的错误信息:" + e.getMessage());            }            post(new Runnable() {                @Override                public void run() {                    setText(String.format("%.2f", startValue));                }            });        }    }}