package shlm.lmcs.com.lazycat.LazyPage;public class LocalPage {    public LocalShopPage getLocalShopPageInstance() {        return new LocalShopPage();    }    public class LocalShopPage {        /*标题*/ String title;        /*价格*/ String price;        /*图片地址*/ String imgUrl;        /*商品的状态*/ String status;        public String getStatus() {            return status;        }        public void setStatus(String status) {            this.status = status;        }        public String getImgUrl() {            return imgUrl;        }        public void setImgUrl(String imgUrl) {            this.imgUrl = imgUrl;        }        public String getPrice() {            return price;        }        public void setPrice(String price) {            this.price = price;        }        public String getTitle() {            return title;        }        public void setTitle(String title) {            this.title = title;        }    }}