package shlm.lmcs.com.lazycat.LazyShopAct;import android.annotation.SuppressLint;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.AdapterView;import android.widget.BaseAdapter;import android.widget.EditText;import android.widget.ListView;import android.widget.TextView;import android.widget.Toast;import com.baidu.location.BDAbstractLocationListener;import com.baidu.location.BDLocation;import com.baidu.mapapi.SDKInitializer;import com.baidu.mapapi.map.MapView;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Tools;import shlm.lmcs.com.lazycat.R;public class InputAddr extends LazyCatAct {    private EditText edit_inputsearch;    private TextView btn_search;/*搜索*/    private ListView list_view;    private MapView map;/*地图控件*/    @SuppressLint("NewApi")    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        SDKInitializer.initialize(getApplicationContext());        setContentView(R.layout.activity_inputaddr);        setTransparentBar();        list_view = findViewById(R.id.activity_inputaddrListView);/*显示地址列表的控件*/        edit_inputsearch = findViewById(R.id.activity_inputaddrforsearch);/*自定义搜索的地址关键字*/        btn_search = findViewById(R.id.activity_inputaddrBtnSearch);/*搜索按钮*/        map = findViewById(R.id.activity_inputAddrMap);/*地图控件*/        /*初始化边框*/        edit_inputsearch.setBackground(Tools.CreateDrawable(1, "#a9a9a9", "#ffffff", 10));        init();    }    /**     * 系统初始化     */    private void init() {        ShowaddrlistAdapter listAdapter = new ShowaddrlistAdapter();        list_view.setAdapter(listAdapter);        list_view.setOnItemClickListener(new AdapterView.OnItemClickListener() {            @Override            public void onItemClick(AdapterView<?> parent, View view, int position, long id) {                Values values = (Values) view.getTag();                Toast.makeText(getApplicationContext(), "携带的信息为:" + values.getCity(), Toast                        .LENGTH_SHORT).show();            }        });        edit_inputsearch.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                edit_inputsearch.setText("");/*点击就设置输入的文字为空*/            }        });        btn_search.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Toast.makeText(getApplicationContext(), "搜索按钮点击", Toast.LENGTH_SHORT).show();            }        });    }    /**     * 获取地理位置     */    class GetAddrclass extends BDAbstractLocationListener {        /**/        @Override        public void onReceiveLocation(BDLocation bdLocation) {        }    }    /**     * listView的适配器     */    class ShowaddrlistAdapter extends BaseAdapter {        @Override        public int getCount() {            return 20;        }        @Override        public Object getItem(int position) {            return position;        }        @Override        public long getItemId(int position) {            return position;        }        @Override        public View getView(int position, View convertView, ViewGroup parent) {            Values values = null;            if (convertView != null) {                /*存在布局 不要加载*/                values = (Values) convertView.getTag();                values.setCity("编号:" + position);            } else {                convertView = LayoutInflater.from(getApplicationContext()).inflate(R.layout                        .item_for_inputaddr, null);                values = new Values();                convertView.setTag(values);                values.setCity("编号：" + position);            }            return convertView;        }    }    class Values {        TextView addrCity;        public String getCity() {            return City;        }        public void setCity(String city) {            City = city;        }        String City;    }}