package shlm.lmcs.com.lazycat.LazyShopAct;import android.annotation.SuppressLint;import android.os.Bundle;import android.view.View;import android.widget.EditText;import android.widget.TextView;import android.widget.Toast;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.TextUnt;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Tools;import shlm.lmcs.com.lazycat.R;public class MakebusinessAct extends LazyCatAct {    private TextView btn_make;    private EditText edit_name;    private EditText edit_addr;    private EditText edit_people;    private EditText edit_tel;    @SuppressLint("ResourceType")    @Override    protected void onCreate(Bundle savedInstanceState) {        setContentView(R.layout.activity_makebusiness);        setTransparentBar();        setStatusBar("#ffffff");        /*寻找控件*/        btn_make = findViewById(R.id.activity_makebusiness_btn_make);/*确定预约*/        TextUnt.with(btn_make).setBackground(Tools.CreateDrawable(1, getResources().getString(R                .color.ThemeColor), getResources().getString(R.color.ThemeColor), 10))                .setTextColor("#ffffff");        /*店铺全称*/        edit_name = findViewById(R.id.activity_useraddhome_addr_editName);        /*店铺地址*/        edit_addr = findViewById(R.id.activity_makebusiness_editAddr);        /*店铺的电话*/        edit_tel = findViewById(R.id.activity_makebusiness_editTel);        /*店铺的联系人*/        edit_people = findViewById(R.id.activity_makebusiness_editPeople);        init();        super.onCreate(savedInstanceState);    }    private void init() {        Listener();    }    private void Listener() {        edit_name.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                EditText ev = (EditText) v;                if (ev.getText().toString().equals("店铺全称")) {                    ev.setText("");                }            }        });        edit_addr.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                EditText ev = (EditText) v;                if (ev.getText().toString().trim().equals("街道、门牌、大楼、建筑、具体位置")) {                    ev.setText("");                }            }        });        edit_tel.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                EditText ev = (EditText) v;                if (ev.getText().toString().trim().equals("手机/固话")) {                    ev.setText("");                }            }        });        edit_people.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                EditText ev = (EditText) v;                if (ev.getText().toString().trim().equals("您的姓名")) {                    ev.setText("");                }            }        });        /**         * 确定预约办理         */        btn_make.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                if (edit_name.getText().toString().equals("") || edit_name.getText().toString()                        .equals("店铺全称")) {                    Toast.makeText(getApplicationContext(),"店铺名称不能为空",Toast.LENGTH_SHORT).show();                } else if (edit_addr.getText().toString().equals("") || edit_addr.getText()                        .toString().equals("街道、门牌、大楼、建筑、具体位置")) {                    Toast.makeText(getApplicationContext(),"店铺的具体位置不能为空",Toast.LENGTH_SHORT).show();                } else if (edit_tel.getText().toString().equals("") || edit_tel                        .getText().toString().equals("手机/固话")) {                    Toast.makeText(getApplicationContext(),"手机/固话必须要填写",Toast.LENGTH_SHORT).show();                } else if (edit_people.getText().toString().equals("") || edit_people.getText()                        .toString().equals("您的姓名")) {                    Toast.makeText(getApplicationContext(),"您的姓名不能为空",Toast.LENGTH_SHORT).show();                }            }        });    }}