package shlm.lmcs.com.lazycat.LazyShopAct;import android.os.Bundle;import android.widget.LinearLayout;import java.util.ArrayList;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Tools;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Views.AutoLineLayout;import shlm.lmcs.com.lazycat.R;/** * 搜索界面 */public class SearchAct extends LazyCatAct {    private AutoLineLayout everyoneSearch;    private AutoLineLayout searchLog;    @Override    protected void onCreate(Bundle savedInstanceState) {        setContentView(R.layout.activity_search);        setTransparentBar();        super.onCreate(savedInstanceState);        init();    }    private void init() {        ArrayList<String> text_list = new ArrayList<>();        text_list.add("流浪地球");        text_list.add("逍遥散人");        text_list.add("东宫");        text_list.add("EXO");        text_list.add("老番茄");        text_list.add("假面骑士ZI-O");        text_list.add("信誓旦旦");        ArrayList<String> text_list2 = new ArrayList<>();        text_list2.add("流浪地球");        text_list2.add("逍遥散人");        text_list2.add("东宫");        text_list2.add("EXO");        text_list2.add("老番茄");        text_list2.add("假面骑士ZI-O");        text_list2.add("信誓旦旦");        ArrayList<LinearLayout> tv_list = Tools.handleToarraylist(getApplicationContext(),                text_list, 20, 10, 20, 10, "#e9e9e9", "#666666", 13);        ArrayList<LinearLayout> tv_list2 = Tools.handleToarraylist(getApplicationContext(),                text_list2, 20, 10, 20, 10, "#e9e9e9", "#666666", 13);        everyoneSearch = findViewById(R.id.activity_everyone_searchBody);/*大家都在搜*/        searchLog = findViewById(R.id.activity_search_searchLogBody);/*用户的搜索记录*/        for (int i = 0; i < tv_list.size(); i++) {            everyoneSearch.addView(tv_list.get(i));        }        for (int i = 0; i < tv_list2.size(); i++) {            searchLog.addView(tv_list2.get(i));        }    }}