package shlm.lmcs.com.lazycat.LazyShopAct.SystemAct;import android.content.Context;import android.os.Bundle;import android.support.annotation.Nullable;import android.view.View;import android.widget.ImageView;import com.bigkoo.convenientbanner.ConvenientBanner;import com.bigkoo.convenientbanner.holder.CBViewHolderCreator;import com.bigkoo.convenientbanner.holder.Holder;import com.bigkoo.convenientbanner.listener.OnItemClickListener;import com.bumptech.glide.Glide;import com.bumptech.glide.load.engine.DiskCacheStrategy;import java.util.ArrayList;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.R;/** * 关于为什么要加入云仓库配送 */public class SystemAboutwhyJoin extends LazyCatAct {    private ArrayList<String> ar_photos = new ArrayList<String>();    private ConvenientBanner banner;/*轮播控件*/    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setTransparentBar();        setContentView(R.layout.activity_aboutwhyjoin);        banner = findViewById(R.id.activity_aboutwhyjoinBanner);        init();    }    /**     * 界面初始化     */    private void init() {        ar_photos.clear();        ar_photos.add("http://120.79.63.36/Photos/JoinVip/1.png");        ar_photos.add("http://120.79.63.36/Photos/JoinVip/2.png");        ar_photos.add("http://120.79.63.36/Photos/JoinVip/3.png");        ar_photos.add("http://120.79.63.36/Photos/JoinVip/4.png");        banner.setPages(new CBViewHolderCreator() {            @Override            public Object createHolder() {                return new bannerHolder();            }        }, ar_photos).setPageIndicatorAlign(ConvenientBanner.PageIndicatorAlign.ALIGN_PARENT_RIGHT).setOnItemClickListener(new OnItemClickListener() {            @Override            public void onItemClick(int position) {                if (position == (ar_photos.size() - 1)) {                    finish();                }            }        }).setCanLoop(false);    }    class bannerHolder implements Holder<String> {        private ImageView img;        @Override        public View createView(Context context) {            img = new ImageView(context);            img.setScaleType(ImageView.ScaleType.FIT_XY);            return img;        }        @Override        public void UpdateUI(Context context, int position, String data) {            Glide.with(context).load(ar_photos.get(position)).diskCacheStrategy(DiskCacheStrategy.NONE).skipMemoryCache(false).into(img);        }    }}