package shlm.lmcs.com.lazycat.LazyShopAct.SystemAct;import android.graphics.Typeface;import android.os.Bundle;import android.text.Editable;import android.text.TextUtils;import android.text.TextWatcher;import android.view.View;import android.widget.EditText;import android.widget.TextView;import android.widget.Toast;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.TextUnt;import shlm.lmcs.com.lazycat.R;/** * 添加银行卡的窗口界面 */public class SystemAddBankcard extends LazyCatAct {    private EditText input_cardNumber;    private TextView TvcardNumber;    private TextView TvcardPeople;    private EditText input_cardPeople;    @Override    protected void onCreate(Bundle savedInstanceState) {        setTransparentBar();        setContentView(R.layout.activity_addbandcard);        TextUnt.with(this, R.id.assembly_act_headTitle).setText("添加银行卡");        input_cardNumber = findViewById(R.id.activity_addbandcard_cardNumber);/*输入卡号的EditText*/        input_cardPeople = findViewById(R.id.activity_addbandcard_cardSavePeople);/*输入的银行卡所属*/        TvcardNumber = findViewById(R.id.activity_addbandcard_number);/*显示银行卡号*/        TvcardPeople = findViewById(R.id.activity_addbandcard_cardPeople);/*显示姓名*/        init();        super.onCreate(savedInstanceState);    }    /**     * 初始化     */    private void init() {        /*设置字体*/        Typeface typeface = Typeface.createFromAsset(getApplicationContext().getAssets(),                "font/price.ttf");        if (typeface != null) {            input_cardNumber.setTypeface(typeface);        } else {            Toast.makeText(getApplicationContext(), "为NULL", Toast.LENGTH_SHORT).show();        }        listener();    }    /**     * 监听事件     */    private void listener() {        /**         * 退出监听事件         */        findViewById(R.id.assembly_act_headBackImg).setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                finish();            }        });        /**         * 监听事件  改变卡号码         */        input_cardNumber.addTextChangedListener(new TextWatcher() {            @Override            public void beforeTextChanged(CharSequence s, int start, int count, int after) {                TvcardNumber.setText("");            }            @Override            public void onTextChanged(CharSequence s, int start, int before, int count) {            }            @Override            public void afterTextChanged(Editable s) {                if (!TextUtils.isEmpty(s)) {                    TvcardNumber.setText(s.toString());                } else {                    TvcardNumber.setText("****************");                }            }        });        /**         * 监听事件 改变卡姓名         */        input_cardPeople.addTextChangedListener(new TextWatcher() {            @Override            public void beforeTextChanged(CharSequence s, int start, int count, int after) {                TvcardPeople.setText("");            }            @Override            public void onTextChanged(CharSequence s, int start, int before, int count) {            }            @Override            public void afterTextChanged(Editable s) {                if (!TextUtils.isEmpty(s)) {                    TvcardPeople.setText("持卡人:" + s.toString());                } else {                    TvcardPeople.setText("持卡人:***");                }            }        });        /*监听点击事件*/    }}