package shlm.lmcs.com.lazycat.LazyShopAct.SystemAct;import android.annotation.SuppressLint;import android.os.Bundle;import android.view.View;import android.widget.ImageView;import android.widget.LinearLayout;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.TextUnt;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Tools;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.WxPay.WxpayinitInstance;import shlm.lmcs.com.lazycat.R;/** * 加入VIP */public class SystemAddtoVip extends LazyCatAct {    @SuppressLint("ResourceType")    @Override    protected void onCreate(Bundle savedInstanceState) {        setStatusBar(getResources().getString(R.color.colorPaywindowBack));        setContentView(R.layout.activity_systemaddtovip);        init();        listener();        super.onCreate(savedInstanceState);    }    /**     * 监听事件     */    @SuppressLint("ResourceType")    private void listener() {        /**         * 退出         */        findViewById(R.id.assembly_act_headBackImg).setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                finish();            }        });        /**         * 点击启动微信支付         */        TextUnt.with(this, R.id.activity_systemaddtoVipBtn_topay).setBackground(Tools                .CreateDrawable(1, getResources().getString(R.color.colorPaywindowBack),                        getResources().getString(R.color.colorPaywindowBack), 5)).setOnClick(new View.OnClickListener() {            @Override            public void onClick(View v) {                WxpayinitInstance wxpayinitInstance = new WxpayinitInstance(                        SystemAddtoVip.this, "云仓库VIP月费", "Vip加盟费用", "7988738574", "15");                wxpayinitInstance.startWxPay();            }        });    }    /**     * 初始化     */    @SuppressLint("NewApi")    private void init() {        ImageView backImg = findViewById(R.id.assembly_act_headBackImg);        backImg.setImageDrawable(Tools.setSvgColor(getApplicationContext(), R.drawable.ico_back,                "#ffffff"));        /*设置支付信息列表框的样式*/        LinearLayout layout = findViewById(R.id.activity_systemaddtoVipPayMsgbody);        layout.setBackground(Tools.CreateDrawable(1, "#ffffff", "#ffffff", 10));    }}