package shlm.lmcs.com.lazycat.LazyShopAct.SystemAct;import android.annotation.SuppressLint;import android.os.Bundle;import android.view.View;import android.widget.ArrayAdapter;import android.widget.Spinner;import android.widget.Toast;import java.util.ArrayList;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.TextUnt;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Tools;import shlm.lmcs.com.lazycat.LazyShopValues.LocalAction;import shlm.lmcs.com.lazycat.R;/** * 系统收银台 */public class SystemCashier extends LazyCatAct {    private String payMoney;/*支付金额*/    private ArrayAdapter<String> spinnerAdapter;    private Spinner spinner;    @SuppressLint("ResourceType")    @Override    protected void onCreate(Bundle savedInstanceState) {        setTransparentBar();        setContentView(R.layout.activity_systemcashier);        /*设置标题*/        TextUnt.with(this, R.id.assembly_act_headTitle).setText("收银台");        TextUnt.with(this, R.id.activity_systemcashierYunCanku).setFontFile(getApplicationContext                (), "canLogo");        /*获取支付金额*/        payMoney = getBundlerValue(LocalAction.WINDOWS_TO_WINDOWS.ACTION_TO_PAYMONEY);        /*设置支付的金额*/        TextUnt.with(this, R.id.activity_systemcashierPaymoney).setText(payMoney);        /*初始化操作的满赠*/        TextUnt.with(this, R.id.activity_systemcashierCankuPayTitle).setBackground(Tools                .CreateDrawable(1, getResources().getString(R.color.colorPrice), getResources()                        .getString(R.color.ThemeColor), 5));        TextUnt.with(this, R.id.activity_systemcashierWxPayTitle).setBackground(Tools                .CreateDrawable(1, getResources().getString(R.color.ThemeColor), getResources()                        .getString(R.color.ThemeColor), 5));        TextUnt.with(this, R.id.activity_systemcashierCashpayTitle).setBackground(Tools                .CreateDrawable(1, getResources().getString(R.color.ThemeColor), getResources()                        .getString(R.color.ThemeColor), 5));        /*下拉选择框*/        spinner = findViewById(R.id.activity_systemcashier_spinner);        init();        listener();        super.onCreate(savedInstanceState);    }    /**     * 初始化操作     */    public void init() {        /*模拟获取到了用户的抵用券*/        /*显示下拉选择框*/        ArrayList<String> spinnerList = new ArrayList<String>();        spinnerList.add("满200立减46元");        spinnerList.add("满1000立减70元");        spinnerList.add("满1500立减100元");        spinnerList.add("满2000立减200元");        spinnerAdapter = new ArrayAdapter<String>(this, R.layout                .support_simple_spinner_dropdown_item, spinnerList);        spinner.setAdapter(spinnerAdapter);    }    /**     * 事件监听     */    public void listener() {        /**         * 退出按钮         */        findViewById(R.id.assembly_act_headBackImg).setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                finish();            }        });        /**         *用户选择微信支付         */        findViewById(R.id.activity_systemcashierBtnPaytoWeixin).setOnClickListener(new View                .OnClickListener() {            @Override            public void onClick(View v) {                Toast.makeText(getApplicationContext(), "微信支付", Toast.LENGTH_SHORT).show();            }        });        /**         * 用户选择云仓库余额支付         */        findViewById(R.id.activity_systemcashierBtnPaytoCangku).setOnClickListener(new View                .OnClickListener() {            @Override            public void onClick(View v) {            }        });        /**         * 用户选择现金支付         */        findViewById(R.id.activity_systemcashierBtnPaytoCashier).setOnClickListener(new View                .OnClickListener() {            @Override            public void onClick(View v) {                Toast.makeText(getApplicationContext(), "现金支付", Toast.LENGTH_SHORT).show();            }        });    }    /**     * 显示确定支付     */    public void ShowAlertTopay() {    }}