package shlm.lmcs.com.lazycat.LazyShopAct.SystemAct;import android.graphics.Bitmap;import android.graphics.BitmapFactory;import android.graphics.Typeface;import android.graphics.drawable.Drawable;import android.os.Bundle;import android.support.annotation.Nullable;import android.util.DisplayMetrics;import android.util.Log;import android.view.ViewGroup;import android.view.Window;import android.widget.EditText;import android.widget.ImageView;import android.widget.RelativeLayout;import com.bumptech.glide.Glide;import com.bumptech.glide.request.FutureTarget;import com.bumptech.glide.request.animation.GlideAnimation;import com.bumptech.glide.request.target.ImageViewTarget;import com.bumptech.glide.request.target.SimpleTarget;import com.bumptech.glide.request.target.Target;import jp.wasabeef.blurry.Blurry;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.TextUnt;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Tools;import shlm.lmcs.com.lazycat.LazyShopValues.LocalValues;import shlm.lmcs.com.lazycat.R;/** * 用高斯模块来模糊商品并要求客户说出该商品的品牌 */public class SystemGuessshop extends LazyCatAct {    private String MSG = "SystemGuessshop.java[+]";    private LocalValues.HTTP_ADDRS http_addrs;    private ImageView shopImg;    private RelativeLayout shopImgBody;    private EditText et_input;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        setTransparentBar();        setContentView(R.layout.activity_systemguessshop);        shopImg = findViewById(R.id.activity_systemquessshopBlurryImg);        http_addrs = new LocalValues.HTTP_ADDRS(getApplicationContext());        shopImgBody = findViewById(R.id.activity_systemquessshopBlurryBody);        /**         * 设置IMAGEVIEW的高度适配         */        ViewGroup.LayoutParams bodyParams = shopImgBody.getLayoutParams();        bodyParams.height = Tools.getScrennWidth(getApplicationContext());        bodyParams.width = Tools.getScrennWidth(getApplicationContext());        shopImgBody.setLayoutParams(bodyParams);        et_input = findViewById(R.id.activity_systemguessshopInput);        init();        listener();        super.onCreate(savedInstanceState);    }    private void init() {        /**         * 设置倒计时         */        TextUnt.with(this, R.id.activity_systemguessshopCoundTime).setBackground(Tools.CreateDrawable(1, "#a9a9a9",                "#a9a9a9", 10)).setTextColor("#ffffff");        TextUnt.with(this, R.id.activity_systemguessshopTodayHaveing).setBackground(Tools.CreateDrawable(1, "#a9a9a9"                , "#a9a9a9", 10)).setTextColor("#ffffff");        /**         * 设置输入框         */        Typeface typeface = Typeface.createFromAsset(getApplication().getAssets(), "font/system_ttf" + ".ttf");        et_input.setTypeface(typeface);        et_input.setBackground(Tools.CreateDrawable(1, "#ffffff", "#ffffff", 20));        /**         * 设置高斯模糊         */        try {            Glide.with(getApplicationContext()).load(http_addrs.HTTP_ADDR_IMG_URL + "2019_10_09" + "/2020_1_1_a.jpg").asBitmap().into(new SimpleTarget<Bitmap>() {                @Override                public void onResourceReady(Bitmap resource, GlideAnimation<? super Bitmap> glideAnimation) {                    Blurry.with(SystemGuessshop.this).radius(10).sampling(10).async().from(resource).into(shopImg);                }            });        } catch (Exception e) {            Log.i(MSG, e.getMessage());        }        /**/    }    /**     * 监听事件     */    private void listener() {        setBackStatic(true, new onBacklistener() {            @Override            public Boolean onBack() {                return true;            }        });    }}