package shlm.lmcs.com.lazycat.LazyShopAct.SystemAct;import android.os.Bundle;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ListView;import android.widget.TextView;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyPage.WAIT_ITME_DIALOGPAGE;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.TextUnt;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.XmlBuilder;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Factory.WaitDialog;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Interface.ProgramInterface;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Net;import shlm.lmcs.com.lazycat.LazyShopValues.LocalValues;import shlm.lmcs.com.lazycat.R;/** * 帮助中心 */public class SystemHelpus extends LazyCatAct {    private ListView lv_body;    private HelpUsmsgadapter helpUsmsgadapter;/*展示信息适配器*/    private String MSG = "SystemHelpus.java[+]";    private LocalValues.HTTP_ADDRS http_addrs;    @Override    protected void onCreate(Bundle savedInstanceState) {        setStatusBar("#ffffff");        setContentView(R.layout.activity_systemhelpus);        lv_body = findViewById(R.id.activity_systemhelpusListView);        http_addrs = new LocalValues.HTTP_ADDRS(getApplicationContext());        init();        super.onCreate(savedInstanceState);    }    /**     * 初始化界面信息     */    private void init() {        /**         * 连接服务器获取数据信息         */        XmlBuilder.XmlInstance xmlInstance = XmlBuilder.getXmlinstanceBuilder(false);        xmlInstance.initDom();        xmlInstance.overDom();        Net.doPostXml(getApplicationContext(), http_addrs.HTTP_ADDR_GET_SYSTEMHELPMSG, new ProgramInterface() {            @Override            public void onSucess(String data, int code, WaitDialog.RefreshDialog _refreshDialog) {                _refreshDialog.dismiss();                Log.e(MSG, "获取帮助信息回传信息:" + data.trim());            }            @Override            public WaitDialog.RefreshDialog onStartLoad() {                final WaitDialog.RefreshDialog refreshDialog = new WaitDialog.RefreshDialog                        (SystemHelpus.this);                WAIT_ITME_DIALOGPAGE wait_itme_dialogpage = new WAIT_ITME_DIALOGPAGE();                wait_itme_dialogpage.setImg(R.id.item_wait_img);                wait_itme_dialogpage.setView(R.layout.item_wait);                wait_itme_dialogpage.setTitle(R.id.item_wait_title);                refreshDialog.Init(wait_itme_dialogpage);                refreshDialog.showRefreshDialog("请稍后...", false);                return refreshDialog;            }            @Override            public void onFaile(String data, int code) {            }        }, xmlInstance.getXmlTree());        /*设置导航的标题*/        TextUnt.with(this, R.id.assembly_act_headTitle).setText("帮助中心");        /*创建适配器*/        helpUsmsgadapter = new HelpUsmsgadapter();        lv_body.setAdapter(helpUsmsgadapter);    }    /**     * 处理帮助信息的界面的信息列表的适配器     */    class HelpUsmsgadapter extends BaseAdapter {        @Override        public int getCount() {            return 30;        }        @Override        public Object getItem(int position) {            return position;        }        @Override        public long getItemId(int position) {            return position;        }        @Override        public View getView(int position, View convertView, ViewGroup parent) {            ValuesId valuesId;            if (convertView != null) {                valuesId = (ValuesId) convertView.getTag();            } else {                valuesId = new ValuesId();                convertView = LayoutInflater.from(getApplicationContext()).inflate(R.layout                        .item_showhelpusmsg, null, false);                valuesId._number = convertView.findViewById(R.id.item_showhelpusmsgNumber);/*数字序号*/                convertView.setTag(valuesId);            }            /*设置序号*/            TextUnt.with(valuesId._number).setText(position++ + ".");            return convertView;        }    }    class ValuesId {        TextView _number;    }}