package shlm.lmcs.com.lazycat.LazyShopAct.SystemAct;import android.annotation.SuppressLint;import android.os.Bundle;import android.support.annotation.Nullable;import android.view.View;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.TextUnt;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Tools;import shlm.lmcs.com.lazycat.R;/** * 显示积分余额界面 */public class SystemIntegral extends LazyCatAct {    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        setStatusBar("#efefef");        setContentView(R.layout.activity_integralbalance);        setBackListener(R.id.activity_integralbalanceBtnback);        super.onCreate(savedInstanceState);        init();        listener();    }    /**     * 初始化界面     */    @SuppressLint("ResourceType")    private void init() {        /*充值的按钮样式*/        TextUnt.with(this, R.id.activity_integralbalanceBtnRecharge).setBackground(Tools.CreateDrawable(1,                getResources().getString(R.color.ThemeColor), getResources().getString(R.color.ThemeColor), 5));        /*提现的按钮样式*/        TextUnt.with(this, R.id.activity_integralbalanceBtnDeposit).setBackground(Tools.CreateDrawable(1, "#e9e9e9",                "#e9e9e9", 5)).setTextColor(getResources().getString(R.color.ThemeColor));    }    /**     * 监听事件     */    private void listener() {        /**         * 查看积分详情         */        TextUnt.with(this, R.id.activity_integralbalanceBtnaboutIntegral).setOnClick(new View.OnClickListener() {            @Override            public void onClick(View v) {                LazyCatActStartActivity(SystemIntegralDetiled.class, false);            }        });    }}