package shlm.lmcs.com.lazycat.LazyShopAct.SystemAct;import android.annotation.SuppressLint;import android.os.Bundle;import android.view.View;import android.widget.ImageView;import android.widget.Toast;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.RelativeLayoutUnt;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.TextUnt;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Tools;import shlm.lmcs.com.lazycat.LazyShopView.SystemIntegerView;import shlm.lmcs.com.lazycat.R;/** * 实现积分体现的功能 */public class SystemIntegralPostal extends LazyCatAct {    private SystemIntegerView IntegerNumber;/*积分显示的控件*/    @Override    protected void onCreate(Bundle savedInstanceState) {        setTransparentBar();        setContentView(R.layout.activity_integralpostal);        IntegerNumber = findViewById(R.id.activity_integralpostalInteger);        init();        listener();        super.onCreate(savedInstanceState);    }    /**     * 程序初始化     */    @SuppressLint("NewApi")    private void init() {        ImageView ico_back = findViewById(R.id.activity_integralpostalBtnBack);        ico_back.setImageDrawable(Tools.setSvgColor(getApplicationContext(), R.drawable.ico_back,                "#ffffff"));        /**         * 执行数字增加动画         */        Tools.NumberAddAnimator(0.00f, 2018.28f, IntegerNumber);    }    /**     * 监听事件     */    private void listener() {        RelativeLayoutUnt.with(this, R.id.activity_integralpostalBtnBrand).setOnclick(new View                .OnClickListener() {            @Override            public void onClick(View v) {                LazyCatActStartActivity(SystemAddBankcard.class, false);            }        });        /**         * 加入Vip的按钮         */        findViewById(R.id.activity_integralpostalBtnAddvip).setOnClickListener(new View                .OnClickListener() {            @Override            public void onClick(View v) {                LazyCatActStartActivity(SystemAddtoVip.class, false);            }        });        /**         * 积分投资板块         */        findViewById(R.id.activity_integralpostalBtnInvest).setOnClickListener(new View                .OnClickListener() {            @Override            public void onClick(View v) {                Toast.makeText(getApplicationContext(), "实在不好意思,该板块云仓库暂时没有开放哦!请等待开通哦", Toast                        .LENGTH_SHORT).show();            }        });        /**         * 要求积分提现         */        findViewById(R.id.activity_integralpostalBtnDeposit).setOnClickListener(new View                .OnClickListener() {            @Override            public void onClick(View v) {                Float integral = TextUnt.with(SystemIntegralPostal.this, R.id                        .activity_integralpostalInteger).getTexttoFloat();                if (integral >= 10) {                    LazyCatActStartActivity(SystemIntegralDeposit.class, false);                } else {                    Toast.makeText(getApplicationContext(), "实在不好意思,您的积分太少无法提现。最低提现金额为100积分",                            Toast.LENGTH_SHORT).show();                }            }        });    }}