package shlm.lmcs.com.lazycat.LazyShopAct.SystemAct;import android.annotation.SuppressLint;import android.os.Bundle;import android.view.View;import android.widget.ImageView;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.RelativeLayoutUnt;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Tools;import shlm.lmcs.com.lazycat.LazyShopView.SystemIntegerView;import shlm.lmcs.com.lazycat.R;/** * 实现积分体现的功能 */public class SystemIntegralPostal extends LazyCatAct {    private SystemIntegerView IntegerNumber;/*积分显示的控件*/    @Override    protected void onCreate(Bundle savedInstanceState) {        setTransparentBar();        setContentView(R.layout.activity_integralpostal);        IntegerNumber = findViewById(R.id.activity_integralpostalInteger);        init();        super.onCreate(savedInstanceState);    }    /**     * 程序初始化     */    @SuppressLint("NewApi")    private void init() {        ImageView ico_back = findViewById(R.id.activity_integralpostalBtnBack);        ico_back.setImageDrawable(Tools.setSvgColor(getApplicationContext(), R.drawable.ico_back,                "#ffffff"));        /**         * 执行数字增加动画         */        Tools.NumberAddAnimator(0.00f, 2018.28f, IntegerNumber);        listener();    }    /**     * 监听事件     */    private void listener() {        RelativeLayoutUnt.with(this, R.id.activity_integralpostalBtnBrand).setOnclick(new View                .OnClickListener() {            @Override            public void onClick(View v) {                LazyCatActStartActivity(SystemAddBankcard.class, false);            }        });    }}