package shlm.lmcs.com.lazycat.LazyShopAct.SystemAct;import android.annotation.SuppressLint;import android.os.Bundle;import android.support.annotation.Nullable;import android.widget.Toast;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyClass.SensorManagerHelper;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.ViewSet;import shlm.lmcs.com.lazycat.LazyShopValues.LocalValues;import shlm.lmcs.com.lazycat.R;/** * 摇动界面获取特价商品 */public class SystemShakeshops extends LazyCatAct {    @SuppressLint("ResourceType")    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        setStatusBar(getResources().getString(R.color.ThemeColor));        setContentView(R.layout.activity_systemshakeshops);        init();        listener();        super.onCreate(savedInstanceState);    }    /**     * 监听事件     */    private void listener() {        /**         * 摇动手机的监听器         */        SensorManagerHelper sensorManagerHelper = new SensorManagerHelper(SystemShakeshops.this);        sensorManagerHelper.setOnShakeListener(new SensorManagerHelper.OnShakeListener() {            @Override            public void onShake() {                ViewSet.with(SystemShakeshops.this, R.id.activity_systemshakeshopsIco).setAnimation(LocalValues.VALUES_VIEWS.SHAKE_ANIMATION_2);                Toast.makeText(SystemShakeshops.this, "摇动手机了", Toast.LENGTH_SHORT).show();            }        });    }    /**     * 初始化操作     */    private void init() {    }}