package shlm.lmcs.com.lazycat.LazyShopAct.SystemAct;import android.annotation.SuppressLint;import android.os.Bundle;import android.support.annotation.Nullable;import android.view.TextureView;import android.view.View;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.TextUnt;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Tools;import shlm.lmcs.com.lazycat.LazyShopVip.SystemVip;import shlm.lmcs.com.lazycat.R;/** * 权益中心 */public class Systemequity extends LazyCatAct {    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        setStatusBar("#efefef");        setContentView(R.layout.activity_systemmyequity);        TextUnt.with(this, R.id.assembly_act_headTitle).setText("权益中心");        setBackListener(R.id.assembly_act_headBackImg);        init();        listener();        super.onCreate(savedInstanceState);    }    /**     * 初始化操作     */    @SuppressLint("ResourceType")    private void init() {        /**         * 设置圆形按钮的边框         */        findViewById(R.id.activity_systemmyequity_Roundview).setBackground(Tools.CreateDrawable(1, "#ffffff",                "#ffffff", 50));        SystemVip systemVip = new SystemVip(Systemequity.this);        systemVip.Start(new SystemVip.OnVipcheck() {            @Override            public void onIsvip() {                /*设置卡片的边框*/                findViewById(R.id.activity_systemmyequity_carbody).setBackground(Tools.CreateDrawable(1,                        getResources().getString(R.color.colorVip), getResources().getString(R.color.colorVip), 25));                /*显示过期时间*/                TextUnt.with(Systemequity.this, R.id.activity_systemmyequity_TimeOver).setVisibility(true);                /*显示加盟的IMG*/                findViewById(R.id.activity_systemmyequity_Vipimg).setVisibility(View.VISIBLE);                TextUnt.with(Systemequity.this, R.id.activity_systemmyequity_equity).setText("商品售后·优先配送·积分生效");            }            @Override            public void onIsnovip() {                /*设置卡片的边框*/                findViewById(R.id.activity_systemmyequity_carbody).setBackground(Tools.CreateDrawable(1, "#a9a9a9",                        "#a9a9a9", 25));                /*设置特权*/                TextUnt.with(Systemequity.this, R.id.activity_systemmyequity_equity).setText("您暂无特权");            }            @Override            public void onIsnologin() {            }            @Override            public void onIslogin() {            }        });    }    /**     * 数据监听     */    private void listener() {    }}