package shlm.lmcs.com.lazycat.LazyShopAct;import android.annotation.SuppressLint;import android.os.Bundle;import android.widget.Toast;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Tools;import shlm.lmcs.com.lazycat.LazyShopValues.LocalValues;import shlm.lmcs.com.lazycat.R;public class UploadApp extends LazyCatAct {    @Override    protected void onCreate(Bundle savedInstanceState) {        setContentView(R.layout.uploadapp);        setHideNav();        Toast.makeText(getApplicationContext(),LocalValues.ADDR_SERVICE,Toast.LENGTH_SHORT).show();        setTransparentBar();        init();        super.onCreate(savedInstanceState);    }    @SuppressLint("NewApi")    private void init() {        findViewById(R.id.uploadapp_message).setBackground(Tools.CreateDrawable(1, "#ffffff",                "#ffffff", 20));        /*设置按钮的样式*/        findViewById(R.id.uploadapp_btnupload).setBackground(Tools.CreateDrawable(1, "#f30d88",                "#f30d88", 5));    }}