package shlm.lmcs.com.lazycat.LazyShopFrg;import android.annotation.SuppressLint;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.LinearLayout;import android.widget.TextView;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyClass.LazyCatFragment;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Tools;import shlm.lmcs.com.lazycat.R;public class Deliveryfrg extends LazyCatFragment {    @SuppressLint("NewApi")    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle            savedInstanceState) {        View item = inflater.inflate(R.layout.fragment_delivery, null);        /*assembly_fragment_main_selectedTitle*/        View selectetd = inflater.inflate(R.layout.assembly_fragment_main_selected, null);        LinearLayout body = item.findViewById(R.id.fragment_deliveryBody);        body.addView(selectetd);        TextView tv = selectetd.findViewById(R.id.assembly_fragment_main_selectedTitle);        tv.setText("精 品 优 选");        selectetd.findViewById(R.id.assembly_fragment_main_selectedOneBody).setBackground(Tools                .CreateDrawable(1, "#efefef", "#efefef", 10));        selectetd.findViewById(R.id.assembly_fragment_main_selectedTwoBody).setBackground(Tools                .CreateDrawable(1, "#efefef", "#efefef", 10));        return item;    }}