package shlm.lmcs.com.lazycat.LazyShopInterface;import android.util.Log;import com.baidu.location.BDAbstractLocationListener;import com.baidu.location.BDLocation;public class LocationMapListener extends BDAbstractLocationListener {    private onReceiveLocationListener _onReceiveLocationListener;/*接口*/    private String MSG = "LocationMapListener.java[+]";    @Override    public void onReceiveLocation(BDLocation bdLocation) {        if (bdLocation.hasAddr()) {            if (_onReceiveLocationListener != null) {                _onReceiveLocationListener.onHasAddr(bdLocation);            } else {                Log.e(MSG, "onReceiveLocationListener是空的");            }        }        else{            _onReceiveLocationListener.onNotAddr(bdLocation);        }    }    public void setOnReceiveLocationListener(onReceiveLocationListener listener) {        this._onReceiveLocationListener = listener;    }    public interface onReceiveLocationListener {        void onHasAddr(BDLocation bdLocation);        void onNotAddr(BDLocation bdLocation);/*没有地址信息*/    }}