package shlm.lmcs.com.lazycat.LazyShopPage;/** * 存储商品的参数信息  或者模块值的信息 */public class LocalPage {    private static SecondSmallNavAPage secondSmallNavPage = new SecondSmallNavAPage();    public static SecondSmallNavAPage getSecondSmallNavAPageInstance() {        return secondSmallNavPage;    }    /**     * 定义一个类  用来存储首页中SecondSmallNav中的数据信息     */    public static class SecondSmallNavAPage {        private String SecondSmallAimgUrl;/*小图片的图片地址*/        private String SecondSmallAClickUrl;/*点击的图片地址*/        private String SecondSmallAtitle;/*小图片第一个标题*/        private String SecondSmallAcontext;/*第一个小图片的介绍*/        private String SecondSmallAtitleColor;/*第一个小图片的标题的颜色*/        private String SecondSmallAcontextColor;/*第一个小图片的介绍的颜色*/        public static String BEGIN_XML = "SecondSmallNavA";        public static String XML_TAG_NAVA_IMG = "navA_img";        public static String XML_TAG_NAVA_URL = "navA_url";        public static String XML_TAG_NAVA_TITLE = "navA_title";        public static String XML_TAG_NAVA_CONTEXT = "navA_context";        public static String XML_TAG_NAVA_TITLECOLOR = "navA_titleColor";        public static String XML_TAG_NAVA_CONTEXTCOLOR = "navA_contextColor";        public String getSecondSmallAtitleColor() {            return SecondSmallAtitleColor;        }        public void setSecondSmallAtitleColor(String secondSmallAtitleColor) {            SecondSmallAtitleColor = secondSmallAtitleColor;        }        public String getSecondSmallAcontextColor() {            return SecondSmallAcontextColor;        }        public void setSecondSmallAcontextColor(String secondSmallAcontextColor) {            SecondSmallAcontextColor = secondSmallAcontextColor;        }        public String getSecondSmallAtitle() {            return SecondSmallAtitle;        }        public void setSecondSmallAtitle(String secondSmallAtitle) {            SecondSmallAtitle = secondSmallAtitle;        }        public String getSecondSmallAcontext() {            return SecondSmallAcontext;        }        public void setSecondSmallAcontext(String secondSmallAcontext) {            SecondSmallAcontext = secondSmallAcontext;        }        public String getSecondSmallAClickUrl() {            return this.SecondSmallAClickUrl;        }        public void setSecondSmallAClickUrl(String secondSmallAClickUrl) {            this.SecondSmallAClickUrl = secondSmallAClickUrl;        }        public String getSecondSmallAimgUrl() {            return this.SecondSmallAimgUrl;        }        public void setSecondSmallAimgUrl(String secondSmallAimgUrl) {            this.SecondSmallAimgUrl = secondSmallAimgUrl;        }    }}