package shlm.lmcs.com.lazycat.LazyShopPage;public class LocalShoppage {    /**     * 所有的参数信息     */    String title;/*标题*/    String barcode;/*条码*/    String spec;/*规格*/    String weight;/*净重*/    String company;/*单位*/    String grade;/*等级*/    String ascription;/*归属*/    String pd;/*生产日期*/    String exp;/*保质期*/    String infrom;/*原场地*/    String onlyid;/*唯一表示符号*/    String price;    String retail;/*零售价格*/    String business;/*商品的对接供货商家*/    String _static;/*状态*/    String su;/*起订*/    String tp;/*批发价格*/    String brand;/*品牌*/    String dlp;/*虚线的价格*/    String img;/*图片的地址*/    String splitUnit;/*最小的的组合单位*/    Boolean isOnlyVipPay;    Boolean isPaythis;/*判断是否购买*/    String payHow;/*购买数量*/    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getBarcode() {        return barcode;    }    public void setBarcode(String barcode) {        this.barcode = barcode;    }    public String getSpec() {        return spec;    }    public void setSpec(String spec) {        this.spec = spec;    }    public String getWeight() {        return weight;    }    public void setWeight(String weight) {        this.weight = weight;    }    public String getCompany() {        return company;    }    public void setCompany(String company) {        this.company = company;    }    public String getGrade() {        return grade;    }    public void setGrade(String grade) {        this.grade = grade;    }    public String getAscription() {        return ascription;    }    public void setAscription(String ascription) {        this.ascription = ascription;    }    public String getPd() {        return pd;    }    public void setPd(String pd) {        this.pd = pd;    }    public String getExp() {        return exp;    }    public void setExp(String exp) {        this.exp = exp;    }    public String getInfrom() {        return infrom;    }    public void setInfrom(String infrom) {        this.infrom = infrom;    }    public String getOnlyid() {        return onlyid;    }    public void setOnlyid(String onlyid) {        this.onlyid = onlyid;    }    public String getPrice() {        return price;    }    public void setPrice(String price) {        this.price = price;    }    public String getRetail() {        return retail;    }    public void setRetail(String retail) {        this.retail = retail;    }    public String getBusiness() {        return business;    }    public void setBusiness(String business) {        this.business = business;    }    public String get_static() {        return _static;    }    public void set_static(String _static) {        this._static = _static;    }    public String getSu() {        return su;    }    public void setSu(String su) {        this.su = su;    }    public String getTp() {        return tp;    }    public void setTp(String tp) {        this.tp = tp;    }    public String getBrand() {        return brand;    }    public void setBrand(String brand) {        this.brand = brand;    }    public String getDlp() {        return dlp;    }    public void setDlp(String dlp) {        this.dlp = dlp;    }    public String getImg() {        return img;    }    public void setImg(String img) {        this.img = img;    }    public String getSplitUnit() {        return splitUnit;    }    public void setSplitUnit(String splitUnit) {        this.splitUnit = splitUnit;    }    public Boolean getOnlyVipPay() {        return isOnlyVipPay;    }    public void setOnlyVipPay(Boolean onlyVipPay) {        isOnlyVipPay = onlyVipPay;    }    public Boolean getPaythis() {        return isPaythis;    }    public void setPaythis(Boolean paythis) {        isPaythis = paythis;    }    public String getPayHow() {        return payHow;    }    public void setPayHow(String payHow) {        this.payHow = payHow;    }}