package shlm.lmcs.com.lazycat.LazyShopSystemMonitor;import android.content.Context;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.XmlBuilder;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Factory.WaitDialog;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Interface.ProgramInterface;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Net;/** * 系统的管理核心服务者 检测软件状态 将状态回传到服务器里 */public class SystemMonitor {    public static final String TAG_ERROR = "";/*致命错误*/    public static final String TAG_LOG = "";/*日志*/    public static final String TAG_WORING = "";/*警告*/    public static final String INT_TAG_TAG = "tag";/*服务器键值对*/    public static final String INT_TAG_FOR = "for";/*服务器键值对*/    public static final String INT_TAG_MSG = "msg";/*服务器键值对*/    public static void SaveTag(Context _context, String _tag, String _for, String _msg) {        XmlBuilder.XmlInstance xmlInstance = XmlBuilder.getXmlinstanceBuilder(false);        xmlInstance.initDom();        xmlInstance.setXmlTree(INT_TAG_TAG, _tag);        xmlInstance.setXmlTree(INT_TAG_FOR, _for);        xmlInstance.setXmlTree(INT_TAG_MSG, _msg);        xmlInstance.overDom();        Net.doPostXml(_context, "", new ProgramInterface() {            @Override            public void onSucess(String data, int code, WaitDialog.RefreshDialog _refreshDialog) {            }            @Override            public WaitDialog.RefreshDialog onStartLoad() {                return null;            }            @Override            public void onFaile(String data, int code) {            }        }, xmlInstance.getXmlTree());    }}