package shlm.lmcs.com.lazycat.LazyShopTools;import android.content.Context;import com.bumptech.glide.Glide;import com.bumptech.glide.GlideBuilder;import com.bumptech.glide.load.DecodeFormat;import com.bumptech.glide.module.GlideModule;/** * 修改  使Glide加载不会变绿色的图片 */public class LocalGlideConfig implements GlideModule {    @Override    public void applyOptions(Context context, GlideBuilder builder) {        builder.setDecodeFormat(DecodeFormat.PREFER_ARGB_8888);    }    @Override    public void registerComponents(Context context, Glide glide) {    }}