package shlm.lmcs.com.lazycat.LazyShopValues;public class LocalMerchantXmlvalues {    private String title;    private String text;    private String status;    private String background;    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getText() {        return text;    }    public void setText(String text) {        this.text = text;    }    public String getStatus() {        return status;    }    public void setStatus(String status) {        this.status = status;    }    public String getBackground() {        return background;    }    public void setBackground(String background) {        this.background = background;    }}