package shlm.lmcs.com.lazycat.LazyShopView;import android.annotation.SuppressLint;import android.content.Context;import android.graphics.Typeface;import android.util.AttributeSet;import android.widget.EditText;@SuppressLint("AppCompatCustomView")public class SystemEditText extends EditText {    public SystemEditText(Context context) {        super(context);        Typeface typeface = Typeface.createFromAsset(getContext().getAssets(), "font/system_ttf" + ".ttf");        setTypeface(typeface);    }    public SystemEditText(Context context, AttributeSet attrs) {        super(context, attrs);        Typeface typeface = Typeface.createFromAsset(getContext().getAssets(), "font/system_ttf" + ".ttf");        setTypeface(typeface);    }    public SystemEditText(Context context, AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);        Typeface typeface = Typeface.createFromAsset(getContext().getAssets(), "font/system_ttf" + ".ttf");        setTypeface(typeface);    }}