package shlm.lmcs.com.lazycat.SuperSystemOS.SuperSystemclass;import android.content.Context;import android.util.Log;import org.xmlpull.v1.XmlPullParser;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.XmlBuilder;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Factory.WaitDialog;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Factory.XmlanalysisFactory;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Interface.ProgramInterface;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Net;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Tools;import shlm.lmcs.com.lazycat.LazyShopValues.LocalAction;import shlm.lmcs.com.lazycat.LazyShopValues.LocalValues;/** * 已经取货成功 * 添加订单打印成功 * 可以添加物流信息 */public class Pushgetdone {    private static String MSG = "Pushgetdone.java[+]";    private static String RETURN_CDOE = "";    /**     * 全部取货完毕     *     * @param _context     * @param _account     * @param _onlyId     */    public static void _push(final Context _context, String _account, String _onlyId, final onPush _onpush) {        LocalValues.HTTP_ADDRS http_addrs = new LocalValues.HTTP_ADDRS(_context);        XmlBuilder.XmlInstance xmlInstance = XmlBuilder.getJavaXmlinstanceBuilder(true);        xmlInstance.setXmlTree(LocalAction.ACTION_SUPERPICKINFOR_USERS._ACCOUNT, _account);        xmlInstance.setXmlTree(LocalAction.ACTION_SHOPVALUES.ACTION_SHOPVALUES_ONLYID, _onlyId);        xmlInstance.overJavaDom();        Log.i(MSG, "全部取货完成提交的XML数据信息:" + xmlInstance.getXmlTree().trim());        Net.doPostXml(http_addrs.HTTP_ADDR_SUPER_PUSHPICKSHOPS, new ProgramInterface() {            @Override            public void onSucess(String data, int code, WaitDialog.RefreshDialog _refreshDialog) {                _refreshDialog.dismiss();                Log.i(MSG, "获取到取货成功XML数据:" + data.trim());                XmlanalysisFactory xmlanalysisFactory = new XmlanalysisFactory(data);                xmlanalysisFactory.Startanalysis(new XmlanalysisFactory.XmlanalysisInterface() {                    @Override                    public void onFaile() {                    }                    @Override                    public void onStartDocument(String tag) {                    }                    @Override                    public void onStartTag(String tag, XmlPullParser pullParser, Integer id) {                        try {                            if (tag.equals(LocalAction.ACTION_RETURNCODE)) {                                RETURN_CDOE = pullParser.nextText().trim();                            }                        } catch (Exception e) {                            e.printStackTrace();                        }                    }                    @Override                    public void onEndTag(String tag, XmlPullParser pullParser, Integer id) {                    }                    @Override                    public void onEndDocument() {                        if (RETURN_CDOE.equals(LocalValues.NET_OK)) {                            _onpush._onPushok();                        } else {                            _onpush._onPusherror();                        }                    }                });            }            @Override            public WaitDialog.RefreshDialog onStartLoad() {                return Tools.getShowwait(_context);            }            @Override            public void onFaile(String data, int code) {            }        }, xmlInstance.getXmlTree().trim());    }    public interface onPush {        void _onPushok();        void _onPusherror();    }}