package shlm.lmcs.com.lazycat.SuperSystemOS.SuperSystemclass;import android.content.Context;import android.util.Log;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.XmlBuilder;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Factory.WaitDialog;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Interface.ProgramInterface;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Net;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Tools;import shlm.lmcs.com.lazycat.LazyShopValues.LocalAction;import shlm.lmcs.com.lazycat.LazyShopValues.LocalValues;/** * 没有商品的提交 管理物流和其他的信息 */public class PushnotGoods {    private static String MSG = "PushnotGoods.java[+]";    /**     * 提交没货     *     * @param _context     * @param _account     * @param _onlyId     * @param _onPushonoGoods     */    public static void _push(final Context _context, String _account, String _onlyId,                             final onPushonoGoods _onPushonoGoods) {        LocalValues.HTTP_ADDRS http_addrs = new LocalValues.HTTP_ADDRS(_context);        XmlBuilder.XmlInstance xmlInstance = XmlBuilder.getXmlinstanceBuilder(true);        xmlInstance.setXmlTree(LocalAction.ACTION_LOCALUSERPAGE.ACTION_LOCALUSERPAGE_ACCOUNT, _account);        xmlInstance.setXmlTree(LocalAction.ACTION_SHOPVALUES.ACTION_SHOPVALUES_ONLYID, _onlyId);        xmlInstance.overDom();        Net.doPostXml(http_addrs.HTTP_ADDR_SUPER_PUSHNOTSHOP, new ProgramInterface() {            @Override            public void onSucess(String data, int code, WaitDialog.RefreshDialog _refreshDialog) {                _refreshDialog.dismiss();                Log.i(MSG, "没货提交数据返回:" + data.trim());                if (data.equals(LocalValues.NET_OK)) {                    _onPushonoGoods._onPushok();                } else {                    _onPushonoGoods._onPusherror();                }            }            @Override            public WaitDialog.RefreshDialog onStartLoad() {                return Tools.getShowwait(_context);            }            @Override            public void onFaile(String data, int code) {            }        }, xmlInstance.getXmlTree().trim());    }    public interface onPushonoGoods {        void _onPushok();/*提交成功*/        void _onPusherror();/*提交失败*/    }}