package shlm.lmcs.com.lazycat.TerminalSystemMO.Record;import android.content.Context;import android.util.Log;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.XmlBuilder;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Factory.WaitDialog;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Interface.ProgramInterface;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Net;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Tools;import shlm.lmcs.com.lazycat.LazyShopValues.LocalValues;/** * 系统的访问次数 */public class SystemVs {    private static String MSG = "SystemVs.java[+]";    private static String ACTION_DEVICE = "device";/*设备号*/    /**     * 记录一次系统的访问历史     */    public static void _start(Context _context) {        LocalValues.HTTP_ADDRS http_addrs = new LocalValues.HTTP_ADDRS(_context);        XmlBuilder.XmlInstance xmlInstance = XmlBuilder.getXmlinstanceBuilder(true);        xmlInstance.setXmlTree(ACTION_DEVICE, Tools.getDevice(_context));        xmlInstance.overDom();        Net.doPostXml(http_addrs.HTTP_ADDR_TERMINALSYSTEM_MO_SYSTEMVS, new ProgramInterface() {            @Override            public void onSucess(String data, int code, WaitDialog.RefreshDialog _refreshDialog) {                Log.i(MSG, "记录访问系统信息为:" + data.trim());            }            @Override            public WaitDialog.RefreshDialog onStartLoad() {                return null;            }            @Override            public void onFaile(String data, int code) {            }        }, xmlInstance.getXmlTree());    }}