package shlm.lmcs.com.lazycat.TerminalSystemMO.Record.Useroperaction.System;import android.content.Context;import android.util.Log;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyTools.XmlBuilder;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Factory.WaitDialog;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Interface.ProgramInterface;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.Net;import shlm.lmcs.com.lazycat.LazyShopValues.LocalValues;/** * 添加用户的一个搜索的记录 */public class Search {    public static String ACTION_SEARCH = "search_key";    public static String MSG = "Search.java[+]";    public static void _saveSearch(Context _context, String _searchkey) {        XmlBuilder.XmlInstance xmlInstance = XmlBuilder.getXmlinstanceBuilder(false);        xmlInstance.initDom();        xmlInstance.setXmlTree(ACTION_SEARCH, _searchkey);        xmlInstance.overDom();        LocalValues.HTTP_ADDRS http_addrs = new LocalValues.HTTP_ADDRS(_context);        Net.doPostXml(http_addrs.HTTP_ADDR_TERMINALSYSTEM_MO_SYSTEM_SEARCH, new ProgramInterface() {            @Override            public void onSucess(String data, int code, WaitDialog.RefreshDialog _refreshDialog) {                Log.i(MSG, "记录系统的商品的搜索记录服务器回传数据为:" + data.trim());            }            @Override            public WaitDialog.RefreshDialog onStartLoad() {                return null;            }            @Override            public void onFaile(String data, int code) {            }        }, xmlInstance.getXmlTree().trim());    }}