package shlm.lmcs.com.lazycat.wxapi;import android.content.Intent;import android.os.Bundle;import android.widget.Toast;import com.tencent.mm.opensdk.constants.ConstantsAPI;import com.tencent.mm.opensdk.modelbase.BaseReq;import com.tencent.mm.opensdk.modelbase.BaseResp;import com.tencent.mm.opensdk.openapi.IWXAPI;import com.tencent.mm.opensdk.openapi.IWXAPIEventHandler;import com.tencent.mm.opensdk.openapi.WXAPIFactory;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.lmcs.com.lazycat.LazyCatProgramUnt.WxPay.Utils;public class WXPayEntryActivity extends LazyCatAct implements IWXAPIEventHandler {    private IWXAPI wxapi;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        Toast.makeText(getApplicationContext(),"1",Toast.LENGTH_SHORT).show();        wxapi = WXAPIFactory.createWXAPI(this, Utils.app_id);        wxapi.handleIntent(getIntent(),this);    }    @Override    protected void onNewIntent(Intent intent) {        super.onNewIntent(intent);        setIntent(intent);        wxapi.handleIntent(intent,this);    }    @Override    public void onReq(BaseReq baseReq) {    }    @Override    public void onResp(BaseResp resp) {        if (resp.getType() == ConstantsAPI.COMMAND_PAY_BY_WX) {            Toast.makeText(this, ""+resp.errCode, Toast.LENGTH_SHORT).show();        }    }}